mapper.update:

UPDATE board
SET boardTitle = #{boardTitle}, boardContent = #{boardContent}
WHERE boardseq = #{boardSeq}


UPDATE adopt
SET adoptImg = #{adoptImg}, adoptSex = #{adoptSex}, adoptRescueDate = TO_DATE(#{adoptRescueDate}, 'yyyy-mm-dd'),
	adoptAge = #{adoptAge}, adoptAddr = #{adoptAddr}, adoptWeight = #{adoptWeight}, adoptNeutor = #{adoptNeutor}, adoptKind = #{adoptKind}
WHERE boardseq = #{boardSeq}

mapper.select:

     SELECT * FROM (SELECT rownum, a.*
     FROM (select rownum as rn, board.boardCategory, board.boardseq, board.boardtitle, adopt.adoptimg, adopt.adoptkind, adopt.adoptaddr, adopt.adoptstate
		   from board, adopt
		   where 1=1
		   and board.boardseq = adopt.boardseq


    	and boardTitle like '%'||#{keyword}||'%'


	    order by board.boardseq desc
	    ) a
	  	WHERE ROWNUM <= ${pageNum} * ${amount}
		)
		WHERE rn > (${pageNum} - 1) * ${amount}

mapper.update:

update board
set boardcnt = boardcnt + 1
where boardseq = #{boardSeq}

mapper.select:

select *
from adopt_req
where boardseq = #{boardSeq} and userseq = #{userSeq}
