mapper.update:
UPDATE USERS SET
userImg = #{userImg, jdbcType=VARCHAR},
userEmail = #{userEmail, jdbcType=VARCHAR}, userPhone = #{userPhone, jdbcType=VARCHAR}, userAddr1 = #{userAddr1, jdbcType=VARCHAR}, userAddr2 = #{userAddr2, jdbcType=VARCHAR}, userAddr3 = #{userAddr3, jdbcType=VARCHAR}, userRole = #{userRole, jdbcType=VARCHAR} WHERE userSeq = #{userSeq}


UPDATE USERS
SET lastDate = sysdate
WHERE userSeq = #{userSeq}


UPDATE users
SET userpw = #{userPw}
where userseq = #{userSeq}

mapper.select:

SELECT * 
FROM USERS 
WHERE USERID = #{userId}
AND USERPW = #{userPw}


SELECT * 
FROM USERS 
WHERE USERID = #{userId} and USEREMAIL = #{userEmail}
